mapper.select:

SELECT  LPAD(NEXTNO, DIGITS, '0')       AS NEXTNO
FROM    NUMBERING_LEDGER
WHERE   NUMBERING_CD     =  #{numberingCode}
AND     AVAIL_YEAR       =  #{availYear}

mapper.update:

UPDATE  NUMBERING_LEDGER
SET     NEXTNO           =  NEXTNO + 1,
        UPDUSR           =  #{user.id},
        UPDDATE          =  CURDATE()
WHERE   NUMBERING_CD     =  #{numberingCode}
AND     AVAIL_YEAR       =  #{availYear}
