mapper.select:

SELECT  CONCAT(RIGHT(AVAIL_YEAR, 2), LPAD(NEXTNO, DIGITS, '0'))     AS nextNo
FROM    NUMBERING_STAMP
WHERE   NUMBERING_CD         =  #{numberingCode}
AND     AVAIL_YEAR           =  #{availYear}

mapper.update:

UPDATE  NUMBERING_STAMP
SET     NEXTNO       =  NEXTNO + 1,
        UPDUSR       =  #{userId},
        UPDDATE      =  CURDATE()
WHERE   NUMBERING_CD =  #{numberingCode}
AND     AVAIL_YEAR   =  #{availYear}
