mapper.select:
SELECT COUNT(*) FROM USERS U INNER JOIN DEPTS DP ON DP.DEPT_CD = U.DEPT_CD AND DP.AVF <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM DEPTS WHERE DEPT_CD = DP.DEPT_CD AND AVF <= CURDATE() AND AVF > DP.AVF ) INNER JOIN POSITIONS POS ON POS.POS_CD = U.POS_CD AND POS.AVF <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM POSITIONS WHERE POS_CD = POS.POS_CD AND AVF <= CURDATE() AND AVF > POS.AVF ) WHERE U.AVF <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM USERS WHERE EMAIL = U.EMAIL AND AVF <= CURDATE() AND AVF > U.AVF )
AND U.EMP_NO = #{empNo}
AND U.NAME = #{name}
AND U.DEPT_CD = #{deptCd}
AND U.POS_CD = #{posCd}

mapper.update:

UPDATE  USERS
SET     PASSWORD     =  #{password},
        PROFILE_IMG  =  #{profileImage.fileName}
WHERE   ID           =  #{id}

mapper.select:
SELECT U.ID AS id, U.EMP_NO AS empNo, U.NAME AS username, DP.DEPT_NM AS deptName, POS.POS_NM AS posName FROM USERS U INNER JOIN DEPTS DP ON DP.DEPT_CD = U.DEPT_CD AND DP.AVF <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM DEPTS WHERE DEPT_CD = DP.DEPT_CD AND AVF <= CURDATE() AND AVF > DP.AVF ) INNER JOIN POSITIONS POS ON POS.POS_CD = U.POS_CD AND POS.AVF <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM POSITIONS WHERE POS_CD = POS.POS_CD AND AVF <= CURDATE() AND AVF > POS.AVF ) WHERE U.AVF <= CURDATE() AND NOT EXISTS ( SELECT 1 FROM USERS WHERE EMAIL = U.EMAIL AND AVF <= CURDATE() AND AVF > U.AVF )
AND U.EMP_NO = #{empNo}
AND U.NAME = #{name}
AND U.DEPT_CD = #{deptCd}
AND U.POS_CD = #{posCd}
ORDER BY U.DEPT_CD, U.EMP_NO LIMIT #{pageFrom}, #{count}
